contracts/ArkreenRECBank.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol';

import "./libraries/TransferHelper.sol";
import "./interfaces/IERC20.sol";
import "./interfaces/IERC20Permit.sol";
import "./interfaces/IWETH.sol";

// Import this file to use console.log
//import "hardhat/console.sol";

struct IncomeInfo {
    uint128     priceForSale;           // 1 ART -> X Payment token
    uint128     amountReceived;         // Amount of payment token received
}

struct SaleInfo {
    address     controller;             // Address of the ART token controller
    address     fundReceiver;           // Address of the receiver while withdrawing the sale income  
    uint128     amountDeposited;        // The amount of ART deposited to this bank contract
    uint128     amountSold;             // The amount of ART already sold out
}

struct Signature {
    address     token;
    uint256     value;
    uint256     deadline;  
    uint8       v;
    bytes32     r;
    bytes32     s;              
}

contract ArkreenRECBank is
    OwnableUpgradeable,
    UUPSUpgradeable
{
    // Public variables
    address public tokenNative;                                             // The wrapped token of the Native token, such as WETH, WMATIC
    mapping(address => bool) public forwarders;                             // All forwarders acceptable
    mapping(address => mapping(address => IncomeInfo)) public saleIncome;   // Mapping X-ART -> Payment Token -> SaleInfo, price zero means not-supported
    mapping(address => SaleInfo) public artSaleInfo;                        // All ART deposit and sale info. If deposit is zero, it means not-supported

    // Events
    event ARTSold(address indexed artToken, address indexed payToken, uint256 artAmount, uint256 payAmount);
    event ARTPriceChanged(address indexed artToken, address indexed payToken, uint256 newPrice);   
    event Deposit(address indexed artToken, uint256 amountDeposit);    
    event Withdraw(address indexed artToken, address indexed payToken, uint256 balance);   

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address native) external virtual initializer {
        __Ownable_init_unchained();
        __UUPSUpgradeable_init(); 
        tokenNative = native;       
    }

    function postUpdate() external onlyProxy onlyOwner 
    {}

    function _authorizeUpgrade(address newImplementation) internal virtual override onlyOwner
    {}    

    receive() external payable {
        assert(msg.sender == tokenNative); // only accept WMATIC via fallback from the WMATIC contract
    }

    function buyART(
        address             tokenPay,
        address             tokenART,
        uint256             amountPay,
        uint256             amountART,
        bool                isExactPay
    ) external {
        _buyART (msg.sender, _msgSender(), tokenPay, tokenART, amountPay, amountART, isExactPay);
    }

    /** 
     * @dev Buy the ART token, then offset the bought ART and mint a cliamte badge.
     * @param tokenART The address of the ART token. There may be serveral different ART tokens in AREC ecosystem.
     * @param amountART The amount of the ART token.
     *                  if isExactPay is true, amountART means the minumum ART token to receive, which may be zero for no checking.
     *                  if isExactPay is false, amountART is the amount of ART token to receive.
     * @param isExactPay Which amount is the exact amount
     *                  = true,  msg.value is the exact amount of the payment token to pay.
     *                  = false, amountART is the exact amount of the ART token to receive.
     */
    function buyARTNative(
        address             tokenART,
        uint256             amountART,
        bool                isExactPay
    ) external payable {

        uint256 priceSale = saleIncome[tokenART][tokenNative].priceForSale;
        require (priceSale !=0, 'ARBK: Payment token not allowed');
        
        uint256 amountPay =  msg.value;
        address receiver = _msgSender();
        if(isExactPay) {
            uint256 amountARTReal = amountPay * (10**9) / priceSale;                    // ART decimal is always 9, so hardcoded here
            require (amountARTReal >= amountART, 'ARBK: Get Less');

            saleIncome[tokenART][tokenNative].amountReceived += uint128(amountPay);     // Native Token already received

            TransferHelper.safeTransfer(tokenART, receiver, amountARTReal);
            artSaleInfo[tokenART].amountSold += uint128(amountARTReal);

            emit ARTSold(tokenART, tokenNative, amountARTReal, amountPay);
        } else {
            uint256 amountPayReal = (amountART * priceSale + (10**9) -1) / (10**9);       // ART decimal is always 9, so hardcoded here
            require (amountPay >= amountPayReal, 'ARBK: Pay Less');                       // amountPay plays as the maximum to pay

            saleIncome[tokenART][tokenNative].amountReceived += uint128(amountPayReal);

            TransferHelper.safeTransfer(tokenART, receiver, amountART);
            artSaleInfo[tokenART].amountSold += uint128(amountART);

            if(amountPay > amountPayReal) TransferHelper.safeTransferETH(msg.sender, amountPay - amountPayReal);

            emit ARTSold(tokenART, tokenNative, amountART, amountPayReal);
        }
    }

    function buyARTWithPermit(
        address             tokenART,
        uint256             amountART,
        bool                isExactPay,
        Signature calldata  permitToPay
    ) external  {                       // Deadline will be checked by router, no need to check here.
        // Permit payment token
        address payer = _msgSender();
        IERC20Permit(permitToPay.token).permit(payer, address(this), 
                        permitToPay.value, permitToPay.deadline, permitToPay.v, permitToPay.r, permitToPay.s);

        // Transfer payement 
        _buyART(payer, payer, permitToPay.token, tokenART, permitToPay.value, amountART, isExactPay);
    }

    function _buyART(
        address             payer,
        address             receiver,
        address             tokenPay,
        address             tokenART,
        uint256             amountPay,
        uint256             amountART,
        bool                isExactPay
    ) internal {

        // priceSale: 1 ART = priceSale (Payment Tokens), for example:
        // 1 ART = 5 USDC, priceSale = 5 000 000
        // 1 ART = 8 MATIC, priceSale = 8 * (10**18), as decial of MATIC is 18 
        uint256 priceSale = saleIncome[tokenART][tokenPay].priceForSale;
        require (priceSale != 0, 'ARBK: Payment token not allowed');
        
        if(isExactPay) {
            uint256 amountARTReal = amountPay * (10**9) / priceSale;          // ART decimal is always 9, so hardcoded here
            require (amountARTReal >= amountART, 'ARBK: Get Less');           // amountART is the minimum ART desired to receive

            TransferHelper.safeTransferFrom(tokenPay, payer, address(this), amountPay);
            saleIncome[tokenART][tokenPay].amountReceived += uint128(amountPay);    // Assmume never overflow, as it is big as (3.4 *10**20)

            TransferHelper.safeTransfer(tokenART, receiver, amountARTReal);
            artSaleInfo[tokenART].amountSold += uint128(amountARTReal);

            emit ARTSold(tokenART, tokenPay, amountARTReal, amountPay);
        } else {
            // The minimum payment is 1 (Payment Token) to avoid attack buying very small amount of ART tokens
            uint256 amountPayReal = (amountART * priceSale + (10**9) -1 ) / (10**9);    // ART decimal is always 9, so hardcoded here            
            require (amountPayReal <= amountPay, 'ARBK: Pay Less');                     // amountPay is the maximum payment 

            TransferHelper.safeTransferFrom(tokenPay, payer, address(this), amountPayReal);
            saleIncome[tokenART][tokenPay].amountReceived += uint128(amountPayReal);    // Assmume never overflow

            TransferHelper.safeTransfer(tokenART, receiver, amountART);
            artSaleInfo[tokenART].amountSold += uint128(amountART);

            emit ARTSold(tokenART, tokenPay, amountART, amountPayReal);
        }
    }

    /**
     * @dev Change the ART sale price based on the buyInToken. Price-zero means not-supporting
     * @param artToken Address of the ART token to sell. The bank contract support multiple ART tokens.
     * @param buyInToken Address of the payment token used to buy ART. 
     * @param price Price of the ART token priced in payment token, 1 ART = X payment token.  
    */
    function changeSalePrice(address artToken, address buyInToken, uint256 price ) external {
        require (msg.sender == artSaleInfo[artToken].controller, "ARBK: Not allowed");
        saleIncome[artToken][buyInToken].priceForSale = uint128(price);
        emit ARTPriceChanged(artToken, buyInToken, price);    
    }  

    /**
     * @dev Add new type of ART token and the controller, only can be called by the owner
     * @param artToken Token address of the ART to add
     * @param controller Address of the controller of the ART token
     */
    function addNewART(address artToken, address controller) external onlyOwner {
        require (controller != address(0), "ARBK: Zero Address");
        require (artSaleInfo[artToken].controller == address(0), "ARBK: Already Added");
        artSaleInfo[artToken].controller = controller;
    }  

    /**
     * @dev Change/shift the controller of the ART token
     * @param artToken Address of the ART token to change controller
     * @param newController Address of the new controller
     */
    function changeARTOwner(address artToken, address newController) external {
        address caller = msg.sender;
        require( caller ==  owner() || caller == artSaleInfo[artToken].controller, "ARBK: Not allowed");
        require (newController != address(0), "ARBK: Zero Address");

        artSaleInfo[artToken].controller = newController;
    }  

    /**
     * @dev Deposit various ART token to the bank, only callable by the controller.
     * @param artToken Token address of the ART to deposit. 
     * @param amountDeposit Amount of the ART to deposit.
     */
    function depositART(address artToken, uint256 amountDeposit) external {
        require (msg.sender == artSaleInfo[artToken].controller, "ARBK: Not allowed");

        uint256 amount = artSaleInfo[artToken].amountDeposited;
        require ((amount = (amount + amountDeposit)) < type(uint128).max, "ARBK: Deposit overflowed" );

        TransferHelper.safeTransferFrom(artToken, msg.sender, address(this), amountDeposit);
        artSaleInfo[artToken].amountDeposited = uint128(amount);
        emit Deposit(artToken, amountDeposit);
    }  

    /**
     * @dev Withdraw all the sales income
     * @param artToken Address of the ART token to withdraw
     * @param payToken Address of the payment token to withdraw
     */
    function withdraw(address artToken, address payToken) external {
        require (msg.sender == artSaleInfo[artToken].controller, "ARBK: Not allowed");

        address receiver = artSaleInfo[artToken].fundReceiver;
        if (receiver == address(0)) receiver = msg.sender;

        uint256 balance = saleIncome[artToken][payToken].amountReceived;
        if (payToken == tokenNative) {
            uint256 amountNative=  IERC20(tokenNative).balanceOf(address(this));
            IWETH(tokenNative).withdraw(amountNative);
            TransferHelper.safeTransferETH(receiver, balance);
        }    
        else {
            TransferHelper.safeTransfer(payToken, receiver, balance);
        }
        emit Withdraw(artToken, payToken, balance);    
    }

    /**
     * @dev Set the income receiver address
     * @param artToken Address of the ART token to set receiver
     * @param receiver Address of the receiver
     */
    function setFundReceiver(address artToken, address receiver) external {
        require (msg.sender == artSaleInfo[artToken].controller, "ARBK: Not allowed");
        artSaleInfo[artToken].fundReceiver = receiver;
    } 

    function _msgSender() internal override view returns (address sender) {
        sender = msg.sender;
        if (msg.data.length >= 20 && forwarders[sender]) {
            assembly {
                sender := shr(96,calldataload(sub(calldatasize(),20)))
            }
        }    
    }

    function setForwarder(address forwarder, bool active) external onlyOwner {
        forwarders[forwarder] = active;
    }

}

contracts/ArkreenBuilder.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import '@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/utils/StringsUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol';

import './ArkreenBuilderStorage.sol';
import "./interfaces/IPausable.sol";

import "./libraries/TransferHelper.sol";
import "./interfaces/IERC20.sol";
import "./interfaces/IWETH.sol";

import "./interfaces/IERC20Permit.sol";
import "./ArkreenBuilderTypes.sol";
import "./interfaces/IArkreenRECBank.sol";
import "./interfaces/IFeSwapRouter.sol";
import "./interfaces/ISwapRouter.sol";
import "./interfaces/IArkreenRECToken.sol";

// Import this file to use console.log
// import "hardhat/console.sol";

contract ArkreenBuilder is
    OwnableUpgradeable,
    UUPSUpgradeable,
    ArkreenBuilderStorage
{
    using AddressUpgradeable for address;

    // Public variables
    string public constant NAME = 'Arkreen Climate Actor';

    // Events

    // Modifiers
    modifier ensure(uint deadline) {
        require(deadline >= block.timestamp, 'ARB: EXPIRED');
        _;
    }
  
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address router, address sales, address native) external virtual initializer {
        __Ownable_init_unchained();
        __UUPSUpgradeable_init();     
        routerSwap = router;
        artBank = sales;
        tokenNative = native;
    }   

    function postUpdate(address sales) external onlyProxy onlyOwner {
        artBank = sales;
    }

    function _authorizeUpgrade(address newImplementation) internal virtual override onlyOwner
    {}

    receive() external payable {
        assert(msg.sender == tokenNative); // only accept WMATIC via fallback from the WMATIC contract
    }  

    /** 
     * @dev Offset the specified amount of ART tokens to create a climate action.
     * @param tokenART Address of the ART token. There may be serveral different ART tokens in AREC ecosystem.
     * @param amountART Amount of the ART token to offset.
     * @param deadline Deadline to handle the transaction.
     */
    function actionBuilderWithART(
        address             tokenART,
        uint256             amountART,
        uint256             deadline
    ) external ensure(deadline) {

        // Transfer payement: bytes4(keccak256(bytes('transferFrom(address from ,address to ,uint256 amount)')));
        bytes memory data1 = abi.encodeWithSelector(0x23b872dd, msg.sender, address(this), amountART);
        (bool success, bytes memory data) = tokenART.call(abi.encodePacked(data1, address(this)));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TransferHelper: TRANSFER_FROM_FAILED');

        // commitOffset(uint256 amount): 0xe8fef571
        bytes memory callData = abi.encodeWithSelector(0xe8fef571, amountART);

        _offsetART(tokenART, abi.encodePacked(callData, _msgSender()));
    }
   

    /** 
     * @dev Buy the ART token with specified token, then offset the bought ART to create a climate action.
     * @param tokenPay The address of the token to pay for the ART token.
     * @param tokenART The address of the ART token. There may be serveral different ART tokens in AREC ecosystem.
     * @param amountPay The amount of the payment token. 
     *                  if modeAction bit0 is true, amountPay should be paid to swap tokenART.
     *                  if modeAction bit0 is false, amountPay means the maximum amount to pay. 
     * @param amountART The amount of the ART token.
     *                  if modeAction bit0 is true, amountART means the minumum ART token to receive, which may be zero for no checking.
     *                  if modeAction bit0 is false, amountART is the amount of ART token to receive.
     * @param modeAction Which amount is the exact amount, and which source to get ART
     *                  bit0 = 1, amountPay is the exact amount of the payment token to pay.
     *                  bit0 = 0, amountART is the exact amount of the ART token to receive.
     *                  bit1 = 0, Swap ART from Dex
     *                  bit1 = 1, But ART from art sales bank
     *                  bit2 = 0, re-pay to _msgSender()
     *                  bit2 = 1, re-pay to msg.sender
     *                  bit3 = 0, Use Uniswap V2 liquidity pool
     *                  bit3 = 1, Use Uniswap V3 liquidity pool    
     *                  Byte4-Byte23, sqrtPriceLimitX96 for Uniswap V3, uint160
     */
    function actionBuilder(
        address             tokenPay,
        address             tokenART,
        uint256             amountPay,
        uint256             amountART,
        uint256             modeAction,
        uint256             deadline
    ) external {               // Deadline will be checked by router, no need to check here. //ensure(permitToPay.deadline)

        // Transfer payement 
        TransferHelper.safeTransferFrom(tokenPay, msg.sender, address(this), amountPay);
        _actionBuilder (tokenPay, tokenART, amountPay, amountART, modeAction, deadline);
    }

    /** 
     * @dev Buy the ART token with Native token, then offset the bought ART.
     * @param tokenART The address of the ART token. There may be serveral different ART tokens in the AREC ecosystem.
     * @param amountART The amount of the ART token.
     *                  if modeAction bit0 is true, amountART means the minumum ART token to receive, which may be zero for no checking.
     *                  if modeAction bit0 is false, amountART is the amount of ART token to receive.
     * @param modeAction Which amount is the exact amount, and which source to get ART
     *                  bit0 = 1, amountPay is the exact amount of the payment token to pay.
     *                  bit0 = 0, amountART is the exact amount of the ART token to receive.
     *                  bit1 = 0, Swap ART from Dex
     *                  bit1 = 1, But ART from art sales bank
     *                  bit2 = 0, re-pay to _msgSender()
     *                  bit2 = 1, re-pay to msg.sender
     *                  bit3 = 0, Use Uniswap V2 liquidity pool
     *                  bit3 = 1, Use Uniswap V3 liquidity pool    
     *                  Byte4-Byte23, sqrtPriceLimitX96 for Uniswap V3, uint160
     */
    function actionBuilderNative(
        address             tokenART,
        uint256             amountART,
        uint256             modeAction,
        uint256             deadline
    ) external payable {               // Deadline will be checked by router, no need to check here.

        // Wrap MATIC to WMATIC  
        IWETH(tokenNative).deposit{value: msg.value}();
        _actionBuilder(tokenNative, tokenART, msg.value, amountART, modeAction, deadline);
    }   

   /** 
     * @dev Buy the ART token with specified token, then offset the bought ART.
     * @param tokenART The address of the ART token. There may be serveral different ART tokens in AREC ecosystem.
     * @param amountART The amount of the ART token.
     *                  if modeAction bit0 is true, amountART means the minumum ART token to receive, which may be zero for no checking.
     *                  if modeAction bit0 is false, amountART is the amount of ART token to receive.
     * @param modeAction Which amount is the exact amount, and which source to get ART
     *                  bit0 = 1, amountPay is the exact amount of the payment token to pay.
     *                  bit0 = 0, amountART is the exact amount of the ART token to receive.
     *                  bit1 = 0, Swap ART from Dex
     *                  bit1 = 1, But ART from art sales bank
     *                  bit2 = 0, re-pay to _msgSender()
     *                  bit2 = 1, re-pay to msg.sender
     *                  bit3 = 0, Use Uniswap V2 liquidity pool
     *                  bit3 = 1, Use Uniswap V3 liquidity pool    
     *                  Byte4-Byte23, sqrtPriceLimitX96 for Uniswap V3, uint160   
     * @param permitToPay The permit information to approve the payment token to swap for ART token 
     */
    function actionBuilderWithPermit(
        address             tokenART,
        uint256             amountART,
        uint256             modeAction,
        Signature calldata  permitToPay
    ) external  {                       // Deadline will be checked by router, no need to check here.
        // Permit payment token
        address payer = msg.sender;
        IERC20Permit(permitToPay.token).permit(payer, address(this), 
                        permitToPay.value, permitToPay.deadline, permitToPay.v, permitToPay.r, permitToPay.s);

        // Transfer payement 
        TransferHelper.safeTransferFrom(permitToPay.token, payer, address(this), permitToPay.value);
        _actionBuilder(permitToPay.token, tokenART, permitToPay.value, amountART, modeAction, permitToPay.deadline);
    }

    /** 
     * @dev Offset the specified amount of ART tokens to create a climate action.
     * @param tokenART Address of the ART token. There may be serveral different ART tokens in AREC ecosystem.
     * @param amountART Amount of the ART token to offset.
     * @param deadline Deadline to handle the transaction.
     * @param badgeInfo The information to be included for climate badge.
     */
    function actionBuilderBadgeWithART(
        address             tokenART,
        uint256             amountART,
        uint256             deadline,
        BadgeInfo calldata  badgeInfo
    ) external ensure(deadline) {

        // Transfer payement: bytes4(keccak256(bytes('transferFrom(address,address,uint256)')));
        bytes memory data1 = abi.encodeWithSelector(0x23b872dd, msg.sender, address(this), amountART);
        (bool success, bytes memory data) = tokenART.call(abi.encodePacked(data1, address(this)));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TransferHelper: TRANSFER_FROM_FAILED');

        // offsetAndMintCertificate(address beneficiary,string offsetEntityID,string beneficiaryID,string offsetMessage,uint256 amount)
        // offsetAndMintCertificate(address,string,string,string,uint256): signature = 0x0fba6a8d
        bytes memory callData = abi.encodeWithSelector(0x0fba6a8d, badgeInfo.beneficiary, badgeInfo.offsetEntityID, 
                                            badgeInfo.beneficiaryID, badgeInfo.offsetMessage, amountART);

        _offsetART(tokenART, abi.encodePacked(callData, _msgSender()));
    }    

    /** 
     * @dev Buy the ART token, then offset the bought ART and mint a cliamte badge.
     * @param tokenPay The address of the token to pay for the ART token.
     * @param tokenART The address of the ART token. There may be serveral different ART tokens in AREC ecosystem.
     * @param amountPay The amount of the payment token. 
     *                  if modeAction bit0 is true, amountPay should be same as the value in permitToPay.
     *                  if modeAction bit0 is false, amountPay means the maximum amount available to pay, if it not zero. 
     * @param amountART The amount of the ART token.
     *                  if modeAction bit0 is true, amountART means the minumum ART token to receive, which may be zero for no checking.
     *                  if modeAction bit0 is false, amountART is the amount of ART token to receive.
     * @param modeAction Which amount is the exact amount, and which source to get ART
     *                  bit0 = 1, amountPay is the exact amount of the payment token to pay.
     *                  bit0 = 0, amountART is the exact amount of the ART token to receive.
     *                  bit1 = 0, Swap ART from Dex
     *                  bit1 = 1, But ART from art sales bank     
     *                  bit2 = 0, re-pay to _msgSender()
     *                  bit2 = 1, re-pay to msg.sender
     *                  bit3 = 0, Use Uniswap V2 liquidity pool
     *                  bit3 = 1, Use Uniswap V3 liquidity pool    
     *                  Byte4-Byte23, sqrtPriceLimitX96 for Uniswap V3, uint160
     * @param badgeInfo The information to be included for climate badge.
     */
    function actionBuilderBadge(
        address             tokenPay,
        address             tokenART,
        uint256             amountPay,
        uint256             amountART,
        uint256             modeAction,
        uint256             deadline,
        BadgeInfo calldata  badgeInfo
    ) external {               // Deadline will be checked by router, no need to check here. //ensure(permitToPay.deadline)

        // Transfer payement
        TransferHelper.safeTransferFrom(tokenPay, msg.sender, address(this), amountPay);

        _actionBuilderBadge (tokenPay, tokenART, amountPay, amountART, modeAction, deadline, badgeInfo);
    }

    /** 
     * @dev Buy the ART token, then offset the bought ART and mint a cliamte badge.
     * @param tokenART The address of the ART token. There may be serveral different ART tokens in AREC ecosystem.
     * @param amountART The amount of the ART token.
     *                  if modeAction bit0 is true, amountART means the minumum ART token to receive, which may be zero for no checking.
     *                  if modeAction bit0 is false, amountART is the amount of ART token to receive.
     * @param modeAction Which amount is the exact amount, and which source to get ART
     *                  bit0 = 1, amountPay is the exact amount of the payment token to pay.
     *                  bit0 = 0, amountART is the exact amount of the ART token to receive.
     *                  bit1 = 0, Swap ART from Dex
     *                  bit1 = 1, But ART from art sales bank
     *                  bit2 = 0, re-pay to _msgSender()
     *                  bit2 = 1, re-pay to msg.sender
     *                  bit3 = 0, Use Uniswap V2 liquidity pool
     *                  bit3 = 1, Use Uniswap V3 liquidity pool    
     *                  Byte4-Byte23, sqrtPriceLimitX96 for Uniswap V3, uint160        
     * @param badgeInfo The information to be included for climate badge.
     */
    function actionBuilderBadgeNative(
        address             tokenART,
        uint256             amountART,
        uint256             modeAction,
        uint256             deadline,
        BadgeInfo calldata  badgeInfo
    ) external payable {               // Deadline will be checked by router, no need to check here. //ensure(permitToPay.deadline)

        // Wrap MATIC to WMATIC  
        IWETH(tokenNative).deposit{value: msg.value}();
        _actionBuilderBadge(tokenNative, tokenART, msg.value, amountART, modeAction, deadline, badgeInfo);
    }

   /** 
     * @dev Buy the ART token, then offset the bought ART and mint a cliamte badge.
     * @param tokenART The address of the ART token. There may be serveral different ART tokens in AREC ecosystem.
     * @param amountART The amount of the ART token.
     *                  if modeAction bit0 is true, amountART means the minumum ART token to receive, which may be zero for no checking.
     *                  if modeAction bit0 is false, amountART is the amount of ART token to receive.
     * @param modeAction Which amount is the exact amount, and which source to get ART
     *                  bit0 = 1, amountPay is the exact amount of the payment token to pay.
     *                  bit0 = 0, amountART is the exact amount of the ART token to receive.
     *                  bit1 = 0, Swap ART from Dex
     *                  bit1 = 1, But ART from art sales bank   
     * @param badgeInfo The information to be included for climate badge.
     * @param permitToPay The permit information to approve the payment token to swap for ART token 
     */
    function actionBuilderBadgeWithPermit(
        address             tokenART,
        uint256             amountART,
        uint256             modeAction,
        BadgeInfo calldata  badgeInfo,
        Signature calldata  permitToPay
    ) external  {               // Deadline will be checked by router, no need to check here. //ensure(permitToPay.deadline)

        // Permit payment token
        address payer = msg.sender;
        IERC20Permit(permitToPay.token).permit(payer, address(this), 
                        permitToPay.value, permitToPay.deadline, permitToPay.v, permitToPay.r, permitToPay.s);

        // Transfer payement 
        TransferHelper.safeTransferFrom(permitToPay.token, payer, address(this), permitToPay.value);
        _actionBuilderBadge(permitToPay.token, tokenART, permitToPay.value, amountART, modeAction, permitToPay.deadline, badgeInfo);
    }

    function _swapARTToken(
        address             tokenPay,
        address             tokenART,
        uint256             amountPay,
        uint256             amountART,
        uint256             modeAction,
        uint256             deadline
    ) internal returns (uint256) {

        bool isExactIn = (modeAction&0x01) == 0x01;
        if(modeAction & 0x02 != 0x00) {
            IArkreenRECBank(artBank).buyART(tokenPay, tokenART, amountPay, amountART, isExactIn);
        } else {
            if(modeAction & 0x08 == 0x00) {                   // Check UniV2/UniV3 Pool
                address[] memory swapPath = new address[](2);
                swapPath[0] = tokenPay;
                swapPath[1] = tokenART;

                if(isExactIn) {
                    IFeSwapRouter(routerSwap).swapExactTokensForTokens(amountPay, amountART, swapPath, address(this), deadline);
                } else {
                    IFeSwapRouter(routerSwap).swapTokensForExactTokens(amountART, amountPay, swapPath, address(this), deadline);
                }
            } else {
                if(isExactIn) {
                    ISwapRouter.ExactInputSingleParams memory exactInputParams = 
                        ISwapRouter.ExactInputSingleParams({
                            tokenIn:            tokenPay,
                            tokenOut:           tokenART,
                            fee:                3000,
                            recipient:          address(this),
                            deadline:           deadline,
                            amountIn:           amountPay,
                            amountOutMinimum:   amountART,
                            sqrtPriceLimitX96:  uint160(modeAction >> 32)
                        });
                    ISwapRouter(routerSwap).exactInputSingle(exactInputParams);
                } else { 
                    ISwapRouter.ExactOutputSingleParams memory ExactOutputParam = 
                        ISwapRouter.ExactOutputSingleParams({
                            tokenIn:            tokenPay,
                            tokenOut:           tokenART,
                            fee:                3000,
                            recipient:          address(this),
                            deadline:           deadline,
                            amountOut:          amountART,
                            amountInMaximum:    amountPay,
                            sqrtPriceLimitX96:  uint160(modeAction >> 32)
                    });
                    ISwapRouter(routerSwap).exactOutputSingle(ExactOutputParam);
                }
            }
        }

        return isExactIn ? IERC20(tokenART).balanceOf(address(this)) : amountART;
    }

    function _actionBuilder(
        address             tokenPay,
        address             tokenART,
        uint256             amountPay,
        uint256             amountART,
        uint256             modeAction,
        uint256             deadline
    ) internal {

        uint256 amountOffset = _swapARTToken(tokenPay, tokenART, amountPay, amountART, modeAction, deadline);  
 
        // commitOffset(uint256 amount): 0xe8fef571
        bytes memory callData = abi.encodeWithSelector(0xe8fef571, amountOffset);

        address payer = _msgSender();
        _offsetART(tokenART, abi.encodePacked(callData, payer));
 
        // Repay more payment back  
        _payBackOverPayment(tokenPay, payer, modeAction);
    }

    function _actionBuilderBadge(
        address             tokenPay,
        address             tokenART,
        uint256             amountPay,
        uint256             amountART,
        uint256             modeAction,
        uint256             deadline,
        BadgeInfo calldata  badgeInfo
    ) internal {

        uint256 amountOffset = _swapARTToken(tokenPay, tokenART, amountPay, amountART, modeAction, deadline);       

        // offsetAndMintCertificate(address beneficiary,string offsetEntityID,string beneficiaryID,string offsetMessage,uint256 amount)
        // offsetAndMintCertificate(address,string,string,string,uint256): signature = 0x0fba6a8d
        bytes memory callData = abi.encodeWithSelector(0x0fba6a8d, badgeInfo.beneficiary, badgeInfo.offsetEntityID, 
                                            badgeInfo.beneficiaryID, badgeInfo.offsetMessage, amountOffset);

        address payer = _msgSender();
        _offsetART(tokenART, abi.encodePacked(callData, payer));
  
        // Repay more payment back  
        _payBackOverPayment(tokenPay, payer, modeAction);
    }

    function _payBackOverPayment(
        address tokenPay,
        address msgSender,
        uint256 modeAction
    ) internal {
        if(modeAction & 0x01 == 0x00) {      
            address repayTo = (modeAction & 0x04 != 0x00) ? msg.sender : msgSender;

            uint256 amountPayLeft = IERC20(tokenPay).balanceOf(address(this));
            if(amountPayLeft > 0) {
                if((tokenPay == tokenNative) && !address(repayTo).isContract()) {
                    IWETH(tokenNative).withdraw(amountPayLeft);
                    TransferHelper.safeTransferETH(repayTo, amountPayLeft);
                } else {
                    TransferHelper.safeTransfer(tokenPay, repayTo, amountPayLeft);
                }
            }
        }
    }

    /** 
     * @dev Call ART token contract to offset the ART token, and optoinally mint the climate badge according to calldata.
     * @param tokenART Address of the ART token contract. 
     * @param callData Calldata to call ART token.
     */
    function _offsetART(
        address         tokenART,
        bytes   memory  callData
    ) internal {

        (bool success, bytes memory returndata) = tokenART.call(abi.encodePacked(callData, _msgSender()));

        if (!success) {
            if (returndata.length > 0) {
                // solhint-disable-next-line no-inline-assembly
                assembly {
                    let returndata_size := mload(returndata)
                    revert(add(32, returndata), returndata_size)
                }
            } else {
                revert("BLD: Error Call to offsetAndMintCertificate");
            }
        }
    }

    function _msgSender() internal override view returns (address signer) {
        signer = msg.sender;
        if (msg.data.length>=20 && trustedForwarders[signer]) {
            assembly {
                signer := shr(96,calldataload(sub(calldatasize(),20)))
            }
        }    
    }    

    function mangeTrustedForwarder(address forwarder, bool addOrRemove) external onlyOwner {
        require(forwarder != address(0), "BLD: Zero Forwarder");
        trustedForwarders[forwarder] = addOrRemove;
    }      
 
    function approveRouter(address[] memory tokens) external onlyOwner {
        require(routerSwap != address(0), "BLD: No Router");
        for(uint256 i = 0; i < tokens.length; i++) {
            TransferHelper.safeApprove(tokens[i], routerSwap, type(uint256).max);
        }
    }

    function approveArtBank(address[] memory tokens) external onlyOwner {
        require(artBank != address(0), "BLD: No Banker");
        for(uint256 i = 0; i < tokens.length; i++) {
            TransferHelper.safeApprove(tokens[i], artBank, type(uint256).max);
        }
    }

    function getVersion() external pure virtual returns (string memory) {
        return "0.2.0";
    }
}

