// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./ArkreenRECIssuanceType.sol";  

contract ArkreenRECIssuanceStorage {
    address public tokenAKRE;                           // token adddress of AKRE
    address public arkreenRegistry;                     // contracts storing all miner's ownership    
    string  public baseURI;

    mapping(uint256 => RECData) internal allRECData;    // save size
    mapping(uint256 => PayInfo) public allPayInfo;      

    uint256 public allRECIssued;                        // total AREC amount issued by minting AREC NFT
    uint256 public allRECRedeemed;                      // total AREC amount redeemed by retiring AREC NFT
    uint256 public allRECLiquidized;                    // total AREC amount Liquidized by Liquidizing to AREC ERC20 token

    // The REC amount issued by specific issuer
    mapping(address => uint256) public allRECByIssuer;

    // The total payment paid to specific issuer with specific payment token
    mapping(address => mapping(address => uint256)) public paymentByIssuer;

    // All payment tokens acceptable and their price
    // AREC mint/issance price, payment token amount per AREC (Decimal = 9)
    // Ex: 1AREC -> 0.2USDT,  ARECMintPrice = 0.2 * 10**6    
    mapping(address => uint256) public paymentTokenPrice;
    address[] public paymentTokens;

    mapping(address => bool) public AllMVPEntity;

    // keccak256("RECIssuance(address owner,uint256 startTime,uint256 endTime,
    //                        uint256 amountREC,uint256 merkelRoot,string url,
    //                        uint256 nonce,uint256 feeREC,uint256 deadline)");
    bytes32 public constant PERMIT_TYPEHASH = 0xEB053433B86341259C91DE8E051FF855E4AEF3CABE1825EE9F5D9A80315FB700; 

    // This is the keccak-256 hash of "AREC.proxy.ESG" subtracted by 1
    bytes32 internal constant _ESG_EXT_SLOT = 0x6C14EAC8C066761328A8B25C5852066ED51A1332CB48C81DA799E3C09C620C9D;
}

contracts/ArkreenRECToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol';
import '@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol';
import '@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol';
//import '@openzeppelin/contracts/access/AccessControl.sol';

import "./ArkreenRECIssuanceType.sol";
import "./interfaces/IArkreenRECIssuance.sol";
import "./interfaces/IArkreenRegistry.sol";
import "./interfaces/IArkreenBadge.sol";
import "./interfaces/IPausable.sol";

// Import this file to use console.log
//import "hardhat/console.sol";

contract ArkreenRECToken is
    OwnableUpgradeable,
    UUPSUpgradeable,
    ERC20Upgradeable,
    IERC721Receiver
{
    // using SafeMath for uint256;    // seems not necessary
    using AddressUpgradeable for address;

    // Public constant variables
    string public constant NAME = 'Arkreen REC Token';
    string public constant SYMBOL = 'ART';

    uint256 public constant MAX_SKIP = 20;
    uint256 public constant FLAG_OFFSET = 1<<64;

    // Public variables
    address public arkreenRegistry;           // Registry contract storing Arkreen contracts   
    address public issuerREC;                 // Address of issuer of the original REC pre-liquidized    
    uint256 public totalLiquidized;           // Total amount of REC that is liquidized
    uint256 public totalOffset;               // Total amount of REC that is offset 

    address public receiverFee;                // Receiver address to receive the liquidization fee
    uint256 public ratioLiquidizedFee;         // Percentage in basis point (10000) of the liquidization fee

    mapping(uint256 => uint256) public allARECLiquidized;   // Loop of all AREC ID: 1st-> 2nd-> ..-> last-> 1st
    uint256 public latestARECID;                            // NFT ID of the latest AREC added to the loop 
    uint256 public ratioFeeToSolidify;                      // Percentage in basis point (10000) to charge for solidifying ART to AREC NFT

//    uint256 partialARECID;                                // AREC NFT ID partialy offset
//    uint256 partialAvailableAmount;                       // Amount available for partial offset
    uint256 public triggerUpgradeAmount;                    // The amount to trigger solidify upgrade
    address public climateBuilder;

    // Events
    event OffsetFinished(address indexed offsetEntity, uint256 amount, uint256 offsetId);
    event Solidify(address indexed account, uint256 amount, uint256 numberAREC, uint256 feeSolidify);    

    // Modifiers
    modifier ensure(uint deadline) {
        require(deadline >= block.timestamp, 'ART: EXPIRED');
        _;
    }

    modifier whenNotPaused() {
        require(!IPausable(arkreenRegistry).paused(), 'ART: Paused');
        _;
    }
  
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address arkRegistry, address issuer, string calldata name, string calldata symbol) external virtual initializer {
        __Ownable_init_unchained();
        __UUPSUpgradeable_init();        
        if(bytes(symbol).length == 0) {
          __ERC20_init_unchained(NAME, SYMBOL);
        } else {
          __ERC20_init_unchained(name, symbol);          
        }
        arkreenRegistry = arkRegistry;
        issuerREC = issuer;
    }

    function postUpdate() external onlyProxy onlyOwner 
    {}

    function _authorizeUpgrade(address newImplementation) internal virtual override onlyOwner
    {}    

    function decimals() public view virtual override returns (uint8) {
        return 9;
    }

    /**
     * @dev Offset the RE token by burning the tokens
     */
    function commitOffset(uint256 amount) public virtual whenNotPaused returns (uint256 offsetActionId) {
        offsetActionId = _offset(msg.sender, _msgSender(), amount);
    }

    /**
     * @dev Third party contract triggers the RE offset in the approval of the owner
     */
    function commitOffsetFrom(address account, uint256 amount)
        external virtual whenNotPaused returns (uint256 offsetActionId) 
    {
        _spendAllowance(account, _msgSender(), amount);
        offsetActionId = _offset(account, account, amount);
    }
   
    /**
     * @dev Internal offset function of the RE token, the RE tokens are burned
     */
    function _offset(address account, address owner, uint256 amount) internal virtual returns (uint256 offsetActionId) {

        if(totalOffset < triggerUpgradeAmount) {                                // To check whether triggering upgrade
            uint256 offsetAmount = triggerUpgradeAmount - totalOffset;
            offsetAmount = (offsetAmount > amount) ? amount: offsetAmount;
            amount -= offsetAmount;
            _burn(account, offsetAmount);

            // Track total retirement amount in TCO2 factory
            address badgeContractU = IArkreenRegistry(arkreenRegistry).getArkreenRetirement();
            offsetActionId = IArkreenBadge(badgeContractU).registerOffset(owner, issuerREC, offsetAmount, 0);
            totalOffset += offsetAmount;

            emit OffsetFinished(owner, offsetAmount, offsetActionId);
            if(amount == 0) {
                return offsetActionId;
            }
        }

        require(amount != 0, 'ART: Zero Offset');

        address issuanceAREC = IArkreenRegistry(arkreenRegistry).getRECIssuance();
        address badgeContract = IArkreenRegistry(arkreenRegistry).getArkreenRetirement();

        // Track total retirement amount in TCO2 factory
        uint256 steps = 0;
        uint256 curAREC; 
        uint256 amountFilled = 0; 
        uint256 amountRegister;

        uint256 partialAvailableAmount;
        uint256 partialARECID;

        uint256 amountOffset;
        uint256 detailsCounter;

        (partialAvailableAmount, partialARECID) = IArkreenBadge(badgeContract).getDetailStatus(address(this));

        if(amount > partialAvailableAmount) {
            while(steps < MAX_SKIP) {
                if(partialAvailableAmount == 0) {
                    curAREC = allARECLiquidized[latestARECID];        // Get the ID at AREC NFT loop head
                    _remove(latestARECID, curAREC);                   // Remove from the loop
                    IArkreenRECIssuance(issuanceAREC).safeTransferFrom(address(this), badgeContract, curAREC);  // Send to Badge contract

                    (, uint128 amountREC, , ) = IArkreenRECIssuance(issuanceAREC).getRECDataCore(curAREC);
                    partialAvailableAmount = amountREC;
                    partialARECID = curAREC;
                }

                if(amount <= partialAvailableAmount) {
                    if (steps==0) break;   
                    amountRegister = amount;
                } else {
                    amountRegister = partialAvailableAmount;
                }
                
                (detailsCounter, partialAvailableAmount) = 
                                IArkreenBadge(badgeContract).registerDetail(amountRegister, partialARECID, (steps==0));
                steps++;
                amountFilled += amountRegister;
                amount -= amountRegister;

                if(amount==0) break;
            }
        }

        amountOffset = (steps==0) ? amount: amountFilled;
        _burn(account, amountOffset);

        offsetActionId = IArkreenBadge(badgeContract).registerOffset(owner, issuerREC, amountOffset, FLAG_OFFSET+detailsCounter);
        totalOffset += amountOffset;

        emit OffsetFinished(owner, amountOffset, offsetActionId);
    }

    /**
     * @dev Offset the RE token and mint a certificate in the single transaction.
     * @param beneficiary Beneficiary address for whom the RE was offset.
     * @param offsetEntityID ID string of the offset entity.
     * @param beneficiaryID ID string of the beneficiary.
     * @param offsetMessage Message to illustrate the offset intention.
     * @param amount Amount to offset and issue an NFT certificate for.
     */
    function offsetAndMintCertificate(
        address         beneficiary,
        string calldata offsetEntityID,
        string calldata beneficiaryID,
        string calldata offsetMessage,
        uint256         amount
    ) external virtual whenNotPaused {
        
        // Offset the specified amount
        address owner = _msgSender();
        uint256 offsetActionId = _offset(msg.sender, owner, amount);     // maybe called from climate operator, so use msg.sender
        uint256[] memory offsetActionIds = new uint256[](1);
        offsetActionIds[0] = offsetActionId;

        // Issue the offset certificate NFT
        address badgeContract = IArkreenRegistry(arkreenRegistry).getArkreenRetirement();
        IArkreenBadge(badgeContract).mintCertificate(
                        owner, beneficiary, offsetEntityID, beneficiaryID, offsetMessage, offsetActionIds);
    }

    /**
     * @dev Solidify the ART token to AREC NFT.
     * @param amount The amount requesting to solidify
     */
    function solidify(uint256 amount) external virtual whenNotPaused 
                returns (uint256 solidifiedAmount, uint256 numberAREC, uint256 feeSolidify) {

        require(latestARECID != 0, 'ART: No Liquidized AREC');
        bool chargeOn = (receiverFee != address(0)) && (ratioFeeToSolidify != 0);           // To save gas
        if(chargeOn) amount = (amount * 10000) / (10000 + ratioFeeToSolidify);             // Substract the solidify fee 
        
        address solidifier = _msgSender();
        address issuanceAREC = IArkreenRegistry(arkreenRegistry).getRECIssuance();

        uint256 skips = 0;
        uint256 curAREC = allARECLiquidized[latestARECID];
        uint256 preAREC = latestARECID;

        while (skips <= MAX_SKIP) {
            (, uint128 amountREC, , ) = IArkreenRECIssuance(issuanceAREC).getRECDataCore(curAREC);
            uint256 amountAREC = amountREC;

            if(amount < amountAREC) {
                require(solidifiedAmount != 0, 'ART: Amount Too Less');                // Must solidify the oldest AREC first
                if(curAREC == latestARECID) break;
                skips++;
                preAREC = curAREC;
                curAREC = allARECLiquidized[curAREC];
            } else {
                IArkreenRECIssuance(issuanceAREC).safeTransferFrom(address(this), solidifier, curAREC);
                amount -= amountAREC;
                solidifiedAmount += amountAREC;
                numberAREC++;
                curAREC = _remove(preAREC, curAREC);
                if(curAREC == 0) break;
            }
        }

        _burn(solidifier, solidifiedAmount);                    // solidifiedAmount must be more than 0 here, burn once to save gas
        totalLiquidized -= solidifiedAmount;                    // 

        if(chargeOn) {
            feeSolidify = solidifiedAmount * ratioFeeToSolidify / 10000;
            _transfer(solidifier, receiverFee, feeSolidify);
        }

        emit Solidify(solidifier, solidifiedAmount, numberAREC, feeSolidify);      
    }

    /**
     * @dev Remove the AREC NFT specified by curAREC from the liquidized list.
     * @param preAREC The AREC NFT just previous in the list
     * @param curAREC The AREC NFT to remove
     * @return nextAREC the next AREC NFT ID if curAREC is not the last in the list
     *         otherwise, returns 0 while curAREC is the last in the list
     */
    function _remove(uint256 preAREC, uint256 curAREC) internal returns (uint256 nextAREC) {
        nextAREC = allARECLiquidized[curAREC];
        allARECLiquidized[preAREC] = nextAREC;

        if(curAREC == latestARECID) {                                   // if remove last AREC
            latestARECID = (preAREC == latestARECID) ? 0 : preAREC;     // if the last AREC is the only AREC
            nextAREC = 0;
        } 
        delete allARECLiquidized[curAREC];                      // delete the current AREC
    }

     /// @dev Receive hook to liquidize Arkreen RE Certificate into RE ERC20 Token
    function onERC721Received(
        address, /* operator */
        address from,
        uint256 tokenId,
        bytes calldata /* data */
    ) external virtual override whenNotPaused returns (bytes4) {

        // Check calling from REC Manager
        require( IArkreenRegistry(arkreenRegistry).getRECIssuance() == msg.sender, 'ART: Not From REC Issuance');

        (, uint128 amountREC, uint8 status, ) = IArkreenRECIssuance(msg.sender).getRECDataCore(tokenId);
        require(status == uint256(RECStatus.Certified), 'ART: Wrong Status');
        
        if(latestARECID == 0) {
            allARECLiquidized[tokenId] = tokenId;                           // build the loop list
            latestARECID = tokenId;
        } else {
            allARECLiquidized[tokenId] = allARECLiquidized[latestARECID];   // Point to loop head
            allARECLiquidized[latestARECID] = tokenId;                      // Add to the loop
            latestARECID = tokenId;                                         // refresh the newest AREC
        }

        totalLiquidized += amountREC;

        // Prepare liquidization fee 
        uint256 fee = 0;
        if(ratioLiquidizedFee != 0 && receiverFee != address(0)) {
            fee = amountREC * ratioLiquidizedFee / 10000;
            _mint(receiverFee, fee);
        }

        _mint(from, amountREC - fee);

        return this.onERC721Received.selector;
    }

    /**
     * @dev set the ratio of liquidization fee
     */     
    function getARECInfo(uint256 number) external view returns (uint256 numAREC, ARECAmount[] memory amountAREC) {
        amountAREC = new ARECAmount[](number);
        if(latestARECID == 0) return (numAREC, amountAREC);

        address issuanceAREC = IArkreenRegistry(arkreenRegistry).getRECIssuance();
        uint256 curAREC = allARECLiquidized[latestARECID];
        for(uint256 index; index < number; index++) {
            amountAREC[index].ARECID = curAREC;
            (, uint128 amountREC, , ) = IArkreenRECIssuance(issuanceAREC).getRECDataCore(curAREC);
            amountAREC[index].amountREC = amountREC;
            numAREC ++;
            if(curAREC == latestARECID) break;
            curAREC = allARECLiquidized[curAREC];
        }
    }  

    function _msgSender() internal override view returns (address signer) {
        signer = msg.sender;
        if (msg.data.length>=20 && (signer == climateBuilder)) {
            assembly {
                signer := shr(96,calldataload(sub(calldatasize(),20)))
            }
        }    
    }

    /**
     * @dev set the ratio of liquidization fee
     */     
    function setRatioFee(uint256 ratio) external onlyOwner {
        require(ratio <10000, 'ART: Wrong Data');
        ratioLiquidizedFee = ratio;
    }  

    /**
     * @dev Change the REC issuance address
     */     
    function setIssuerREC(address issuer) external onlyOwner {
        require(issuer != address(0), 'ART: Wrong Address');
        issuerREC = issuer;
    }

    /**
     * @dev set the ratio of solidify fee to Solidify from ART to AREC
     */     
    function setRatioFeeToSolidify(uint256 ratio) external onlyOwner {
        require(ratio <10000, 'ART: Wrong Data');
        ratioFeeToSolidify = ratio;
    }  

    /**
     * @dev set the receiver of liquidization fee
     */     
    function setReceiverFee(address receiver) external onlyOwner {
        require(receiver != address(0), 'ART: Wrong Address');
        receiverFee = receiver;
    }

    function setTriggerAmount(uint256 amount) external onlyOwner {
        triggerUpgradeAmount = amount;
    }

    function setClimateBuilder(address builder) external onlyOwner {
        climateBuilder = builder;
    }
}

contracts/ArkreenRECIssuanceExt.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import '@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721EnumerableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol';

import "./interfaces/IMinerRegister.sol";
import "./interfaces/IArkreenRegistry.sol";
import "./interfaces/IArkreenMiner.sol";
import "./interfaces/IArkreenBadge.sol";

import "./interfaces/IERC20.sol";
import "./libraries/TransferHelper.sol";
import "./interfaces/IERC20Permit.sol";
import "./ArkreenRECIssuanceStorage.sol";
import "./interfaces/IPausable.sol";

// Import this file to use console.log
// import "hardhat/console.sol";

contract ArkreenRECIssuanceExt is
    OwnableUpgradeable,
    UUPSUpgradeable,
    ERC721EnumerableUpgradeable,
    ArkreenRECIssuanceStorage
{
    // using SafeMath for uint256;    // seems not necessary
    using AddressUpgradeable for address;

    // Public variables
    string public constant NAME = 'Arkreen RE Certificate';
    string public constant SYMBOL = 'AREC';

    // Events
    event RECRequested(address owner, uint256 tokenId);
    event RECRejected(uint256 tokenId);
    event RECDataUpdated(address owner, uint256 tokenId);
    event RECCertified(address issuer, uint256 tokenId);
    event RECCanceled(address owner, uint256 tokenId);    
    event RECLiquidized(address owner, uint256 tokenId, uint256 amountREC);
    event RedeemFinished(address redeemEntity, uint256 tokenId, uint256 offsetActionId);
    event ESGBatchMinted(address owner, uint256 tokenId);
    event ESGBatchDataUpdated(address owner, uint256 tokenId);

    // Modifiers
    modifier ensure(uint deadline) {
        require(deadline >= block.timestamp, 'RECIssuance: EXPIRED');
        _;
    }

    modifier whenNotPaused() {
        require(!IPausable(arkreenRegistry).paused(), 'AREC: Paused');
        _;
    }    
  
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address _tokenAKRE, address arkRegistry) external virtual initializer {
        __Ownable_init_unchained();
        __UUPSUpgradeable_init();
        __ERC721_init_unchained(NAME, SYMBOL);
        tokenAKRE = _tokenAKRE;
        arkreenRegistry = arkRegistry;
        baseURI = 'https://www.arkreen.com/AREC/' ;
    }

    function postUpdate() external onlyProxy onlyOwner 
    {}

    function _authorizeUpgrade(address newImplementation) internal virtual override onlyOwner
    {}    

    /**
     * @dev To mint the empty AREC NFT as as ESG batch, which must be updated later to be successfully confirmed.
     * @param idAssetType The type of the AREC asset to issue
     * @param amountREC The amount if the AREC asset target to issue.
     * permitToPay Payment info to mint the AREC NFT
     */
    function mintESGBatch(
        uint256   idAssetType,
        uint256   amountREC,
        Signature calldata permitToPay
    ) external ensure(permitToPay.deadline) whenNotPaused returns (uint256 tokenId) {

        // Check the caller be the MVP enity
        address sender = _msgSender();
        require( AllMVPEntity[sender], "AREC: Not Allowed");

        // Check and get asset information
        (address issuer, , address tokenPay, uint128 rateToIssue, ) 
                                                = IArkreenRegistry(arkreenRegistry).getAssetInfo(idAssetType);

        // Check payment appoval
        require( permitToPay.token == tokenPay, "AREC: Wrong Payment Token");

        uint256 valuePayment = amountREC * rateToIssue / ( 10**9);              // Rate is caluated based 10**9

        if(permitToPay.value != 0) {
          require( permitToPay.value >= valuePayment, "AREC: Low Payment Value");

          IERC20Permit(permitToPay.token).permit(sender, address(this), 
                          permitToPay.value, permitToPay.deadline, permitToPay.v, permitToPay.r, permitToPay.s);
        }

        tokenId = totalSupply() + 1;
        _safeMint(sender, tokenId);

        // Prepare REC data
        RECData memory recData;
        recData.issuer =  issuer;
        recData.minter = sender;
        recData.amountREC =  uint128(amountREC);
        recData.status = uint8(RECStatus.Pending);
        recData.idAsset = uint16(idAssetType);

        allRECData[tokenId] = recData;

        PayInfo memory payInfo = PayInfo({token: permitToPay.token, value: valuePayment});
        allPayInfo[tokenId] = payInfo;

        emit ESGBatchMinted(sender, tokenId);

        // Transfer the REC mint fee
        TransferHelper.safeTransferFrom(permitToPay.token, _msgSender(), address(this), valuePayment);
    }

    /**
     * @dev To update the REC NFT mint info while it is rejected by the the issuer.
     * tokenId The ID of the REC NFT to update
     */
    function updateRECDataExt(
        uint256         tokenID,
        uint32          startTime,
        uint32          endTime,
        string calldata cID,
        string calldata region,
        string calldata url,
        string calldata memo) external whenNotPaused
    {
        // Check the caller be the MVP enity
        address sender = _msgSender();
        require( AllMVPEntity[sender], "AREC: Not Allowed");

        // Only ESG_AREC owner allowed to change the REC data
        require(ownerOf(tokenID) == _msgSender(), 'AREC: Not Owner');     // owner should be the minter also

        // Only Pending and Rejected ESG_AREC can be updated
        RECData storage recData = allRECData[tokenID];
        require(recData.status <= uint8(RECStatus.Rejected), 'AREC: Wrong Status');

        if(startTime != 0) recData.startTime = startTime;
        if(endTime != 0) recData.endTime = endTime;
        if(bytes(cID).length != 0) recData.cID = cID;
        if(bytes(region).length != 0) recData.region = region;
        if(bytes(url).length != 0) recData.url = url;
        if(bytes(memo).length != 0) recData.memo = memo;

        recData.status =  uint8(RECStatus.Pending);
        emit ESGBatchDataUpdated(_msgSender(), tokenID);
    }

    /**
     * @dev To cancel the REC NFT mint request,only can be called the NFT owner.
     * REC NFT mint fee is refund to the owner after the transaction.
     * tokenId The ID of the REC NFT to update
     */
    function cancelRECRequest(uint256 tokenID) external whenNotPaused {

        // Only REC owner allowed to cancel the request
        require(ownerOf(tokenID) == _msgSender(), 'AREC: Not Owner');

        // Only pending REC can be cancelled
        require(allRECData[tokenID].status == uint8(RECStatus.Rejected), 'AREC: Wrong Status');  

        allRECData[tokenID].status = uint8(RECStatus.Cancelled);

        // Refund the request fee
        TransferHelper.safeTransfer(allPayInfo[tokenID].token, _msgSender(), allPayInfo[tokenID].value);

        // delete the payment info to save storage
        delete allPayInfo[tokenID];
        emit RECCanceled(_msgSender(), tokenID);
    }

    /// @dev return all the AREC issaunce token/price list
    function allARECMintPrice() external view virtual returns (RECMintPrice[] memory) {
        uint256 sizePrice = paymentTokens.length;
        RECMintPrice[] memory ARECMintPrice = new RECMintPrice[](sizePrice);

        for(uint256 index; index < sizePrice; index++) {
          address token = paymentTokens[index];
          ARECMintPrice[index].token = paymentTokens[index];
          ARECMintPrice[index].value = paymentTokenPrice[token];
        }
        return ARECMintPrice;
    }

    /**
     * @dev Add or remove the MVP addresses 
     * @param op The operation of MVP address, =true, add MVP; =false, remove MVP 
     * @param listMVP The list of the MVP addresses
     */
    function manageMVPAddress(bool op, address[] calldata listMVP) public whenNotPaused onlyOwner {
        for(uint256 index; index < listMVP.length; index++) {
            require( AllMVPEntity[listMVP[index]] != op, "AREC: Wrong Status" );
            AllMVPEntity[listMVP[index]] = op;
        }
    }

    /**
     * @dev Hook that is called before any token transfer.
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual override (ERC721EnumerableUpgradeable) {

        // Only certified REC can be transferred
        if(from != address(0)) {
            RECData storage recData = allRECData[tokenId];
            if(recData.status == uint8(RECStatus.Liquidized)) {
                address issuerREC = recData.issuer;
                address tokenREC = IArkreenRegistry(arkreenRegistry).getRECToken(issuerREC, recData.idAsset);
                address arkreenBadge = IArkreenRegistry(arkreenRegistry).getArkreenRetirement();

                // Only the ART contract can restore the AREC
                require(msg.sender == tokenREC, 'AREC: Not Allowed');

                if(to == arkreenBadge) {
                    recData.status = uint8(RECStatus.Retired);
                } else {
                    uint256 amountREC = recData.amountREC;

                    // Modified the Liquidized REC amount
                    allRECLiquidized -= amountREC;

                    // Set the AREC status to be Liquidized
                    recData.status = uint8(RECStatus.Certified);
                }
            }
            else {
                require(recData.status == uint8(RECStatus.Certified), 'AREC: Wrong Status');
            }
        }
        super._beforeTokenTransfer(from, to, tokenId);
    }
}

contracts/ArkreenMiner.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721EnumerableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol';

import "./libraries/TransferHelper.sol";
import "./interfaces/IERC20.sol";
import "./interfaces/IERC20Permit.sol";
import "./ArkreenMinerTypes.sol";

contract ArkreenMiner is 
    OwnableUpgradeable,
    UUPSUpgradeable,
    ERC721EnumerableUpgradeable
{
    using AddressUpgradeable for address;

    // Constants
    string public constant NAME = 'Arkreen Miner';
    string public constant SYMBOL = 'AKREM';

    // keccak256("RemoteMinerOnboard(address owner,address miners,address token,uint256 price,uint256 deadline)");
    bytes32 public constant REMOTE_MINER_TYPEHASH = 0xE397EAA556C649D10F65393AC1D09D5AA50D72547C850822C207516865E89E32;  

    // keccak256("RemoteMinerOnboardBatch(address owner,uint256 quantity,address token,uint256 value,uint256 deadline)");
    bytes32 public constant REMOTE_MINER_BATCH_TYPEHASH = 0x9E7E2F63BB8D2E99F3FA05B76080E528E9CA50746A4383CDF2803D633AFF18A6;  

    // keccak256("StandardMinerOnboard(address owner,address miner,uint256 deadline)");
    bytes32 public constant STANDARD_MINER_TYPEHASH = 0x73F94559854A7E6267266A158D1576CBCAFFD8AE930E61FB632F9EC576D2BB37;  

    uint256 public constant MAX_BATCH_SALE = 50;

    // Public variables
    bytes32 public DOMAIN_SEPARATOR;
    uint256 public totalStandardMiner;                  // Total amount of standard miner
    string public baseURI;
    address public tokenAKRE;                           // Token adddress of AKRE
    address public tokenNative;                         // The wrapped token of the Native token, such as WETH, WMATIC

    // All registered miner manufactures
    mapping(address => bool) public AllManufactures;

    // All miner infos
    mapping(uint256 => Miner) public AllMinerInfo;

    // All managers with various privilege
    mapping(uint256 => address) public AllManagers;

    // Mapping from miner address to the respective token ID
    mapping(address => uint256) public AllMinersToken;

    // Miner white list mapping from miner address to miner type
    mapping(address => uint8) public whiteListMiner;

    uint256 public totalSocketMiner;                  // Total amount of socket miner

    // Miner white list for sales in batch, mapping from index to miner address
    mapping(uint256 => address) private whiteListMinerBatch;
    uint256 private whiteListBatchIndexHead;
    uint256 private whiteListBatchIndexTail;

    // Events
    event MinerOnboarded(address indexed owner, address indexed miner);
    event MinerOnboardedBatch(address indexed owner, address[] minersBatch);
    event StandardMinerOnboarded(address indexed owner, address indexed miner);
    event RemoteMinersInBatch(address[] owners, address[] miners);
    event SocketMinerOnboarded(address indexed owner, address indexed miner);

    modifier ensure(uint256 deadline) {
        require(block.timestamp <= deadline, 'Arkreen Miner: EXPIRED');
        _;
    }

    modifier onlyMinerManager() {
        require(_msgSender() == AllManagers[uint256(MinerManagerType.Miner_Manager)], 'Arkreen Miner: Not Miner Manager');
        _;
    }    

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address _tokenAKRE, address _tokenNative, address _minerManager, address _minerAuthority)
        external
        virtual
        initializer
    {
        __Ownable_init_unchained();
        __UUPSUpgradeable_init();
        __ERC721_init_unchained(NAME, SYMBOL);
        tokenAKRE = _tokenAKRE;
        tokenNative = _tokenNative;
        AllManagers[uint256(MinerManagerType.Miner_Manager)] = _minerManager;
        AllManagers[uint256(MinerManagerType.Register_Authority)] = _minerAuthority;
        baseURI = 'https://www.arkreen.com/miners/';

        DOMAIN_SEPARATOR = keccak256(
            abi.encode(
                keccak256('EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)'),
                keccak256(bytes("Arkreen Miner")),
                keccak256(bytes('1')),
                block.chainid,
                address(this)
            )
        );  
    }

    function postUpdate() external onlyProxy onlyOwner 
    {}

    function _authorizeUpgrade(address newImplementation)
        internal
        virtual
        override
        onlyOwner
    {}

    /**
     * @dev Onboarding a remote Miner paid with Native token (MATIC)
     * @param owner address receiving the remote miner
     * @param miner address of the remote miner onboarding
     * @param permitMiner signature of the miner register authority to confirm the miner address and price.  
     */
    function RemoteMinerOnboardNative(
        address     owner,
        address     miner,
        Signature   memory  permitMiner
    ) external payable ensure(permitMiner.deadline) {

        // Check payment value
        require( (tokenNative != address(0)) && (tokenNative == permitMiner.token) && 
                  (msg.value == permitMiner.value), "Arkreen Miner: Payment error");

        // Check for minting remote miner  
        _mintRemoteMinerCheck(owner, miner, permitMiner);

        // mint new remote miner
        _mintRemoteMiner(owner, miner);
        emit MinerOnboarded(owner, miner);
    }    

    /**
     * @dev Onboarding remote miners in batch, orderly fetched from the batch whitelist, paid with Native token (MATIC)
     * @param owner address receiving the remote miners
     * @param numMiners number of remote miners desired to purchase
     * @param permitMiner signature of the miner register authority to confirm the owner and sales price.  
     */

    function RemoteMinerOnboardNativeBatch(
        address     owner,
        uint8       numMiners,
        Signature   memory  permitMiner
    ) external payable ensure(permitMiner.deadline) {

        require((numMiners != 0) && (numMiners <= numberOfWhiteListBatch()), "Arkreen Miner: Wrong Miner Number");

        // Check payment value
        require( (tokenNative != address(0)) && (tokenNative == permitMiner.token) && 
                  (msg.value == permitMiner.value), "Arkreen Miner: Payment error");

        // Check for remote miner minting price  
        _mintBatchCheckPrice(owner, numMiners, permitMiner);

        // mint new remote miners in batch
        address[] memory minersBatch = _mintRemoteMinerBatch(owner, numMiners);
        emit MinerOnboardedBatch(owner, minersBatch);
    }    

    /**
     * @dev Onboarding a remote miner while the payment has been approved
     * @param owner address receiving the remote miner
     * @param miner address of the remote miner onboarding
     * @param permitMiner signature of miner register authority to confirm the miner address and price.  
     */
    function RemoteMinerOnboardApproved(
        address     owner,
        address     miner,
        Signature   memory  permitMiner
    ) external ensure(permitMiner.deadline) {

        // Check for minting remote miner  
        _mintRemoteMinerCheck(owner, miner, permitMiner);

        // mint new remote miner
        _mintRemoteMiner(owner, miner);

        // Transfer onboarding fee
        address sender = _msgSender();
        TransferHelper.safeTransferFrom(permitMiner.token, sender, address(this), permitMiner.value);

        emit MinerOnboarded(owner, miner);
    }

    /**
     * @dev Onboarding a remote miner while the payment has been approved
     * @param owner address receiving the remote miner
     * @param numMiners number of remote miners desired to purchase
     * @param permitMiner signature of miner register authority to confirm the miner address and price.  
     */
    function RemoteMinerOnboardApprovedBatch(
        address     owner,
        uint8       numMiners,
        Signature   memory  permitMiner
    ) external ensure(permitMiner.deadline) {

        // Check for minting remote miner  
        _mintBatchCheckPrice(owner, numMiners, permitMiner);

        // mint new remote miner
        address[] memory minersBatch = _mintRemoteMinerBatch(owner, numMiners);

        // Transfer onboarding fee
        address sender = _msgSender();
        TransferHelper.safeTransferFrom(permitMiner.token, sender, address(this), permitMiner.value);

        emit MinerOnboardedBatch(owner, minersBatch);
    }

    /**
     * @dev Check for minting a remote Miner
     * @param owner address receiving the remote miner
     * @param miner address of the remote miner onboarding
     * @param permitMiner signature of miner register authority to confirm the miner address and price.  
     */
    function _mintRemoteMinerCheck( 
        address     owner,
        address     miner,
        Signature   memory  permitMiner
    ) view internal {

        // Check miner is white listed  
        require(whiteListMiner[miner] == uint8(MinerType.RemoteMiner), 'Arkreen Miner: Wrong Miner');
        require(AllMinersToken[miner] == 0, "Arkreen Miner: Miner Repeated");

        // Check signature
        // keccak256("RemoteMinerOnboard(address owner,address miners,address token,uint256 price,uint256 deadline)");
        bytes32 hashRegister = keccak256(abi.encode(REMOTE_MINER_TYPEHASH, owner, miner, 
                                          permitMiner.token, permitMiner.value, permitMiner.deadline));
        bytes32 digest = keccak256(abi.encodePacked('\x19\x01', DOMAIN_SEPARATOR, hashRegister));
        address recoveredAddress = ecrecover(digest, permitMiner.v, permitMiner.r, permitMiner.s);

        require(recoveredAddress != address(0) && 
                recoveredAddress == AllManagers[uint256(MinerManagerType.Register_Authority)], 'Arkreen Miner: INVALID_SIGNATURE');
    }

    /**
     * @dev Check the remote miner minting authorization, including owner, quantity and sale value
     * @param owner address receiving the remote miners in batch
     * @param quantity quantity of remote miner for batch sale
     * @param permitMiner signature of miner register authority to confirm the owner address and value.  
     */
    function _mintBatchCheckPrice( 
        address     owner,
        uint8       quantity,
        Signature   memory  permitMiner
    ) view internal {

        require((quantity != 0) && (quantity <= numberOfWhiteListBatch()), "Arkreen Miner: Wrong Miner Number");
        require( quantity <= MAX_BATCH_SALE, 'Arkreen Miner: Quantity Too More');

        // Check signature
        // keccak256("RemoteMinerOnboardBatch(address owner,uint256 quantity,address token,uint256 value,uint256 deadline)");
        bytes32 hashRegister = keccak256(abi.encode(REMOTE_MINER_BATCH_TYPEHASH, owner, uint256(quantity),
                                          permitMiner.token, permitMiner.value, permitMiner.deadline));
        bytes32 digest = keccak256(abi.encodePacked('\x19\x01', DOMAIN_SEPARATOR, hashRegister));
        address recoveredAddress = ecrecover(digest, permitMiner.v, permitMiner.r, permitMiner.s);

        require(recoveredAddress != address(0) && 
                recoveredAddress == AllManagers[uint256(MinerManagerType.Register_Authority)], 'Arkreen Miner: INVALID_SIGNATURE');
    }

    /**
     * @dev mint a remote Miner
     * @param owner address receiving the remote miner
     * @param miner address of the remote miner onboarding
     */
    function _mintRemoteMiner( address owner, address miner) internal {

        // Prepare to mint new remote miner
        Miner memory newMiner;
        newMiner.mAddress = miner;
        newMiner.mType = MinerType.RemoteMiner;
        newMiner.mStatus = MinerStatus.Normal;
        newMiner.timestamp = uint32(block.timestamp);    

        // mint new remote miner
        uint256 realMinerID = totalSupply() + 1;
        _safeMint(owner, realMinerID);
        AllMinersToken[miner] = realMinerID;
        AllMinerInfo[realMinerID] = newMiner;

        delete whiteListMiner[miner];
    }

    /**
     * @dev mint a remote Miner
     * @param owner address receiving the remote miner
     * @param numMiners number of remote miners needed to mint
     */
    function _mintRemoteMinerBatch(address owner, uint8 numMiners) internal returns (address[] memory minerList) {

        // Prepare to mint new remote miners
        Miner memory newMiner;
        minerList = new address[](numMiners);

        newMiner.mType = MinerType.RemoteMiner;
        newMiner.mStatus = MinerStatus.Normal;
        newMiner.timestamp = uint32(block.timestamp);   

        uint256 listHead = whiteListBatchIndexHead;

        for(uint8 index; index < numMiners; index++) {
            address miner = whiteListMinerBatch[listHead +index];
            minerList[index] = miner;

            // Check miner is not repeated
            require(AllMinersToken[miner] == 0, "Arkreen Miner: Miner Repeated");

            // mint new remote miner
            uint256 realMinerID = totalSupply() + 1;
            _safeMint(owner, realMinerID);
            AllMinersToken[miner] = realMinerID;
            newMiner.mAddress = miner;
            AllMinerInfo[realMinerID] = newMiner;
            delete whiteListMinerBatch[listHead +index];
        }
        whiteListBatchIndexHead += numMiners;
    }

    /**
     * @dev Onboarding a remote Miner
     * @param owner address receiving the remote miner
     * @param miner address of the remote miner onboarding
     * @param permitMiner signature of miner register authority to confirm the miner address and price.  
     * @param permitToPay signature of payer to pay the onboarding fee
     */
    function RemoteMinerOnboard(
        address     owner,
        address     miner,
        Sig       memory  permitMiner,
        Signature memory  permitToPay
    ) external ensure(permitToPay.deadline) {

        // Check miner is white listed  
        Signature memory fullPermitMiner = Signature(permitToPay.token, permitToPay.value, permitToPay.deadline,
                                            permitMiner.v, permitMiner.r, permitMiner.s);

        _mintRemoteMinerCheck(owner, miner, fullPermitMiner);

        // Permit payment
        address sender = _msgSender();
        IERC20Permit(permitToPay.token).permit(sender, address(this), 
                                        permitToPay.value, permitToPay.deadline, permitToPay.v, permitToPay.r, permitToPay.s);

        // mint new remote miner
        _mintRemoteMiner(owner, miner);

        // Transfer onboarding fee
        TransferHelper.safeTransferFrom(permitToPay.token, sender, address(this), permitToPay.value);

        emit MinerOnboarded(owner, miner);
    }


        /**
     * @dev Onboarding remote miners in batch mode
     * @param owner address receiving the remote miner
     * @param numMiners number of remote miners desired to purchase
     * @param permitMiner signature of miner register authority to confirm the miner address and price.  
     * @param permitToPay signature of payer to pay the onboarding fee
     */
    function RemoteMinerOnboardBatch(
        address     owner,
        uint8       numMiners,
        Sig       memory  permitMiner,
        Signature memory  permitToPay
    ) external ensure(permitToPay.deadline) {

        // Check miner is white listed  
        Signature memory fullPermitMiner = Signature(permitToPay.token, permitToPay.value , permitToPay.deadline,
                                            permitMiner.v, permitMiner.r, permitMiner.s);

        _mintBatchCheckPrice(owner, numMiners, fullPermitMiner);

        // Permit payment
        address sender = _msgSender();
        IERC20Permit(permitToPay.token).permit(sender, address(this), 
                                        permitToPay.value, permitToPay.deadline, permitToPay.v, permitToPay.r, permitToPay.s);

        // mint new remote miner
        address[] memory minersBatch = _mintRemoteMinerBatch(owner, numMiners);

        // Transfer onboarding fee
        TransferHelper.safeTransferFrom(permitToPay.token, sender, address(this), permitToPay.value);

        emit MinerOnboardedBatch(owner, minersBatch);
    }

    /**
     * @dev Onboarding standard miner
     * @param owner address receiving the standard miner
     * @param miner address of the standard miner onboarding
     * @param permitMiner signature of onboarding manager to approve the onboarding
     */
    function StandardMinerOnboard(
        address owner,
        address miner,
        uint256 deadline,
        Sig     calldata permitMiner
    ) external ensure(deadline) {

        // Check the starndard address
        require(!miner.isContract(), 'Arkreen Miner: Not EOA Address');
        require(AllMinersToken[miner] == 0, "Arkreen Miner: Miner Repeated");
        MinerType minerType = MinerType(whiteListMiner[miner]);
        require((minerType == MinerType.StandardMiner) || (minerType == MinerType.SocketMiner), 'Arkreen Miner: Wrong Miner');        

        // Check signature
        bytes32 hashRegister = keccak256(abi.encode(STANDARD_MINER_TYPEHASH, owner, miner, deadline));
        bytes32 digest = keccak256(abi.encodePacked('\x19\x01', DOMAIN_SEPARATOR, hashRegister));
        address recoveredAddress = ecrecover(digest, permitMiner.v, permitMiner.r, permitMiner.s);

        require(recoveredAddress != address(0) && 
                recoveredAddress == AllManagers[uint256(MinerManagerType.Register_Authority)], 'Arkreen Miner: INVALID_SIGNATURE');

        Miner memory tmpMiner;
        tmpMiner.mAddress = miner;
        tmpMiner.mType = minerType;
        tmpMiner.mStatus = MinerStatus.Normal;
        tmpMiner.timestamp = uint32(block.timestamp);        

        // Mint a new standard miner
        uint256 minerID = totalSupply() + 1;
        _safeMint(owner, minerID);
        AllMinersToken[miner] = minerID;
        AllMinerInfo[minerID] = tmpMiner;

        // Increase the counter of total standard/socket miner 
        if(minerType == MinerType.StandardMiner) { 
          totalStandardMiner += 1;
          emit StandardMinerOnboarded(owner,  miner);   // emit onboarding event
        } else {
          totalSocketMiner += 1;
          emit SocketMinerOnboarded(owner,  miner);
        }

        delete whiteListMiner[miner]; 
    }

    /**
     * @dev Onboarding remote miners in batch
     * @param owners addresses receiving the remote miners
     * @param miners addresses of the remote miners onboarding
     */
    function RemoteMinerOnboardInBatch(
        address[]  calldata   owners,
        address[]  calldata   miners
    ) external onlyMinerManager {

        require(owners.length == miners.length, 'Arkreen Miner: Wrong Address List');

        // Prepare to mint new remote miners, only remote miners
        Miner memory newMiner;
        newMiner.mType = MinerType.RemoteMiner;
        newMiner.mStatus = MinerStatus.Normal;
        newMiner.timestamp = uint32(block.timestamp);

        for(uint256 index; index < owners.length; index++) {
            // Mint new remote miners one by one
            uint256 remoteMinerID = totalSupply() + 1;
            newMiner.mAddress = miners[index];
            _safeMint(owners[index], remoteMinerID);
            AllMinersToken[newMiner.mAddress] = remoteMinerID;            
            AllMinerInfo[remoteMinerID] = newMiner;
        }
        // Need to emit? If yes, data may be big 
        emit RemoteMinersInBatch(owners, miners);
    }

    /**
     * @dev Get all the miner info of the specified miner
     * @param addrMiner miner address
     */
    function GetMinerInfo(address addrMiner) external view returns (address owner, Miner memory miner) {
        uint256 minerID = AllMinersToken[addrMiner];
        owner = ownerOf(minerID);
        miner = AllMinerInfo[minerID];
    }

    /**
     * @dev Get all the miner address of the owner
     * @param owner owner address
     */
    function GetMinersAddr(address owner) external view returns (address[] memory minersAddr) {
        uint256 totalMiners = balanceOf(owner);
        minersAddr = new address[](totalMiners);
        for(uint256 index;  index < totalMiners; index++) {     
            uint256 minerID = tokenOfOwnerByIndex(owner, index);
            minersAddr[index] = AllMinerInfo[minerID].mAddress;
        }
    }

    /**
     * @dev Register or unregister miner manufactures
     * @param manufactures manufactures to be registered or unregistered
     * @param yesOrNo = true, to register manufactures, = false, to unregister manufactures
     */
    function ManageManufactures(address[] calldata manufactures, bool yesOrNo) external onlyOwner {
      for(uint256 index;  index < manufactures.length; index++) {
        AllManufactures[manufactures[index]] = yesOrNo;
      }
    }

    /**
     * @dev Update the miner status
     * @param minerID miner ID of any type of miners
     * @param minerStatus new status
     */
    function SetMinersStatus(uint256 minerID, MinerStatus minerStatus) external onlyOwner {
        require(minerStatus != MinerStatus.Pending, 'Arkreen Miner: Wrong Input');      
        AllMinerInfo[minerID].mStatus = minerStatus;
    }

    /**
     * @dev Update the miner white list, add/remove the miners to/from the white list.
     *      Only miners in the white list are allowed to onboard as an NFT.
     * @param typeMiner Type of the miners to add, MinerType.Empty(=0) means to remove the miners
     * @param addressMiners List of the miners
     */
    function UpdateMinerWhiteList(uint8 typeMiner, address[] calldata addressMiners) external onlyMinerManager {
      address tempAddress;
      for(uint256 index; index < addressMiners.length; index++) {
        tempAddress = addressMiners[index];
        if(typeMiner == 0xFF) {
          delete whiteListMiner[tempAddress];
          continue;
        }
        // Checked for non-existence
        require( tempAddress != address(0) && !tempAddress.isContract(), 'Arkreen Miner: Wrong Address');     
        require( whiteListMiner[tempAddress] == 0, 'Arkreen Miner: Miners Repeated');      
        whiteListMiner[tempAddress] = uint8(typeMiner);
      }
    }

    /**
     * @dev Update the miner white list for batch sales. Only miners in the white list are allowed to onboard as an NFT.
     * @param addressMiners List of the miners
     */
    function UpdateMinerWhiteListBatch(address[] calldata addressMiners) external onlyMinerManager {
      uint256 indexStart = whiteListBatchIndexTail;
      uint256 length = addressMiners.length;
      for(uint256 index; index < length; index++) {
        whiteListMinerBatch[indexStart + index] = addressMiners[index];
      }
      whiteListBatchIndexTail += length;
    }

    /**
     * @dev get the length of the white list for batch sales
     */
    function numberOfWhiteListBatch() public view returns (uint256) {
      return whiteListBatchIndexTail - whiteListBatchIndexHead;
    }

    /**
     * @dev Check if holding miners
     * @param owner owner address
     */
    function isOwner(address owner) external view returns (bool) {
        // just considering number of tokens, token status not checked 
        return balanceOf(owner) > 0;
    }

    /**
     * @dev Set the Arkreen managing accounts 
     * @param managerType type of the managing account
     * @param managerAddress address of the managing account     
     */
    function setManager(uint256 managerType, address managerAddress) external onlyOwner {
      AllManagers[managerType] = managerAddress;
    }

    /**
     * @dev Set the native token address
     * @param native address, not checked againt zero address to disable payment by native token    
     */
    function setNativeToken(address native) external onlyOwner {
      tokenNative = native;
    }    

    /**
     * @dev Withdraw all the onboarding fee
     * @param token address of the token to withdraw, USDC/ARKE
     */
    function withdraw(address token) public onlyOwner {
        address receiver = AllManagers[uint256(MinerManagerType.Payment_Receiver)];
        if(receiver == address(0)) {
            receiver = _msgSender();
        }

        if(token == tokenNative) {
            TransferHelper.safeTransferETH(receiver, address(this).balance);      
        } else {
            uint256 balance = IERC20(token).balanceOf(address(this));
            TransferHelper.safeTransfer(token, receiver, balance);
        }
      }

    function tokenURI(uint256 tokenId) public view override returns (string memory){
        return super.tokenURI(tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
        public view override( ERC721EnumerableUpgradeable) returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function setBaseURI(string memory newBaseURI) external virtual onlyOwner {
        baseURI = newBaseURI;
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

}

